item.description:
åŸºäº ring buffer å’Œå†…å­˜æ …æ  __threadfence()ï¼š

æ ¸å¿ƒæ•°æ®ç»“æ„ï¼ˆæ— é” SPSC ç¯å½¢é˜Ÿåˆ—ï¼‰

``` cuda
// lock_free_queue.cuh

#pragma once
#include <cuda_runtime.h>

template <typename T, int CapacityPow2>
struct LockFreeQueueSPSC {
    // è¦æ±‚å®¹é‡æ˜¯ 2 çš„æ•´æ•°æ¬¡å¹‚ï¼Œæ–¹ä¾¿ç”¨ä½ä¸å–æ¨¡
    static_assert((CapacityPow2 & (CapacityPow2 - 1)) == 0,
                  'Capacity must be power of 2.');

    // ç¯å½¢ç¼“å†²åŒº
    T buffer[CapacityPow2];

    // headï¼šä¸‹ä¸€ä¸ªè¦ pop çš„ä½ç½®ç´¢å¼•
    // tailï¼šä¸‹ä¸€ä¸ªè¦ push çš„ä½ç½®ç´¢å¼•
    // çº¦å®šï¼š
    //   - åªæœ‰æ¶ˆè´¹è€…çº¿ç¨‹å†™ head
    //   - åªæœ‰ç”Ÿäº§è€…çº¿ç¨‹å†™ tail
    //   - åŒæ–¹éƒ½å¯ä»¥è¯»å¯¹æ–¹çš„ç´¢å¼•
    unsigned int head;
    unsigned int tail;

    __device__ void init() {
        head = 0;
        tail = 0;
    }

    // pushï¼šåœ¨é˜Ÿåˆ—å°¾éƒ¨æ’å…¥ä¸€ä¸ªå…ƒç´ 
    // è¿”å› true è¡¨ç¤ºæˆåŠŸï¼Œfalse è¡¨ç¤ºé˜Ÿåˆ—å·²æ»¡
    __device__ bool push(const T &value) {
        // ç”Ÿäº§è€…çº¿ç¨‹

        // æœ¬çº¿ç¨‹å†™ tailï¼Œæ— æ•°æ®ç«äº‰
        unsigned int t = tail;
        // è¯»æ¶ˆè´¹è€…çš„ headï¼Œéœ€è¦ä¿è¯ä¸è¢«ç¼–è¯‘å™¨ä¹±ä¼˜åŒ–ï¼Œcast æˆ volatile æŒ‡é’ˆ
        unsigned int h = *reinterpret_cast<volatile unsigned int *>(&head);

        // å¦‚æœ tail - head >= capacity å°±æ»¡äº†
        if (t - h >= CapacityPow2) {
            return false; // full
        }

        // å†™å…¥æ•°æ®åˆ°ç¯å½¢ç¼“å†²åŒº
        buffer[t & (CapacityPow2 - 1)] = value;

        // ä¿è¯ buffer å†™å…¥åœ¨ tail æ›´æ–°å‰å¯¹å…¶ä»–çº¿ç¨‹å¯è§
        __threadfence();

        // æ›´æ–° tailï¼Œè®©æ¶ˆè´¹è€…çœ‹åˆ°æ–°å…ƒç´ 
        *reinterpret_cast<volatile unsigned int *>(&tail) = t + 1;
        return true;
    }

    // popï¼šä»é˜Ÿåˆ—å¤´éƒ¨å–å‡ºä¸€ä¸ªå…ƒç´ 
    // è¿”å› true è¡¨ç¤ºæˆåŠŸï¼Œfalse è¡¨ç¤ºé˜Ÿåˆ—ä¸ºç©º
    __device__ bool pop(T &out) {
        // æ¶ˆè´¹è€…çº¿ç¨‹

        // æœ¬çº¿ç¨‹å†™ headï¼Œæ— æ•°æ®ç«äº‰
        unsigned int h = head;
        // è¯»ç”Ÿäº§è€…çš„ tail
        unsigned int t = *reinterpret_cast<volatile unsigned int *>(&tail);

        if (h == t) {
            return false; // empty
        }

        // åœ¨è¯»å–æ•°æ®å‰ï¼Œç¡®ä¿æˆ‘ä»¬çœ‹åˆ°çš„æ˜¯ç”Ÿäº§è€…å·²ç»å®Œæˆå†™å…¥çš„æ•°æ®
        __threadfence();

        out = buffer[h & (CapacityPow2 - 1)];

        // å†åŠ ä¸€é“ fenceï¼Œç¡®ä¿ out è¯»å®Œåå†ç§»åŠ¨ headï¼ˆæ¯”è¾ƒä¿å®ˆçš„å†™æ³•ï¼‰
        __threadfence();

        *reinterpret_cast<volatile unsigned int *>(&head) = h + 1;
        return true;
    }

    // å½“å‰é˜Ÿåˆ—å…ƒç´ æ•°é‡ï¼ˆè¿‘ä¼¼å€¼ï¼Œéå¼ºä¸€è‡´ï¼‰
    __device__ unsigned int size() const {
        unsigned int h = *reinterpret_cast<const volatile unsigned int *>(&head);
        unsigned int t = *reinterpret_cast<const volatile unsigned int *>(&tail);
        return t - h;
    }

    __device__ bool empty() const {
        return size() == 0;
    }

    __device__ bool full() const {
        return size() >= CapacityPow2;
    }
};
```

example:

``` cuda
// main.cu

#include <cstdio>
#include 'lock_free_queue.cuh'

constexpr int QUEUE_CAPACITY = 1024;  // å¿…é¡»æ˜¯ 2 çš„å¹‚
constexpr int N_ITEMS        = 1000;  // éœ€è¦ä¼ è¾“çš„å…ƒç´ æ•°é‡

__global__ void queue_kernel(int *output) {
    // å•ä¸ª block é‡Œå…±äº«ä¸€ä¸ªé˜Ÿåˆ—å¯¹è±¡ï¼ˆæ”¾åœ¨å…±äº«å†…å­˜é‡Œä¹Ÿè¡Œï¼Œè¿™é‡Œç®€å•æ”¾å…¨å±€ï¼‰
    __shared__ LockFreeQueueSPSC<int, QUEUE_CAPACITY> queue;

    if (threadIdx.x == 0) {
        // ç”Ÿäº§è€…åˆå§‹åŒ–é˜Ÿåˆ—
        queue.init();
    }

    __syncthreads(); // ç¡®ä¿é˜Ÿåˆ—åˆå§‹åŒ–å®Œæ¯•

    if (threadIdx.x == 0) {
        // ç”Ÿäº§è€…
        int produced = 0;
        while (produced < N_ITEMS) {
            if (queue.push(produced)) {
                produced++;
            }
            // å¦‚æœæ»¡äº†ï¼Œä¼šæš‚æ—¶ push å¤±è´¥ï¼Œå°±ç»§ç»­å¾ªç¯é‡è¯•ï¼ˆè‡ªæ—‹ï¼‰
        }
    } else if (threadIdx.x == 1) {
        // æ¶ˆè´¹è€…
        int consumed = 0;
        while (consumed < N_ITEMS) {
            int v;
            if (queue.pop(v)) {
                output[consumed] = v;
                consumed++;
            }
            // å¦‚æœç©ºäº†ï¼Œå°±ç»§ç»­å¾ªç¯ç­‰å¾…æ•°æ®ï¼ˆè‡ªæ—‹ï¼‰
        }
    }
}

int main() {
    int *d_out = nullptr;
    int *h_out = new int[N_ITEMS];

    cudaMalloc(&d_out, N_ITEMS * sizeof(int));

    // å¯åŠ¨ä¸€ä¸ª blockï¼Œè‡³å°‘ä¸¤ä¸ªçº¿ç¨‹ï¼š
    //   thread 0: producer
    //   thread 1: consumer
    queue_kernel<<<1, 2>>>(d_out);
    cudaDeviceSynchronize();

    cudaMemcpy(h_out, d_out, N_ITEMS * sizeof(int), cudaMemcpyDeviceToHost);

    // ç®€å•æ£€æŸ¥ï¼šåº”è¯¥æ˜¯ 0..N_ITEMS-1
    bool ok = true;
    for (int i = 0; i < N_ITEMS; ++i) {
        if (h_out[i] != i) {
            printf('Mismatch at %d: got %d expected %d\n', i, h_out[i], i);
            ok = false;
            break;
        }
    }

    if (ok) {
        printf('Queue works! First 10 values: ');
        for (int i = 0; i < 10 && i < N_ITEMS; ++i) {
            printf('%d ', h_out[i]);
        }
        printf('\n');
    }

    cudaFree(d_out);
    delete[] h_out;
    return 0;
}
