``` python
class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        # æ»‘åŠ¨çª—å£ [a, b] ç¡®å®šå½“å‰çª—å£ä¸­æ— é‡å¤å­—ç¬¦çš„é•¿åº¦
        # hash è¡¨ä¿å­˜å½“å‰çª—å£å­—ç¬¦åŠä½ç½®å¹¶åŒ¹é…ï¼Œ max_num è®°å½•å½“å‰æœ€å¤§å€¼
        if s == '':
            return 0
        elif len(s) == 1:
            return 1

        hash = []
        a,b = 0,0
        max_num = 0
        hash.append(s[0])
        nums = len(s)
        while b < nums - 1:
            # å¦‚æœä¸‹ä¸€ä½å­—ç¬¦ä¸åœ¨hashè¡¨å†…ï¼Œb+=1
            # å¦åˆ™ a è·³è½¬è‡³hashä¸­å­—ç¬¦ä½ç½®
            if s[b+1] in hash:
                tmp = hash.index(s[b+1])
                hash = hash[tmp + 1:]
                b += 1
                hash.append(s[b])
            else:
                hash.append(s[b+1])
                b += 1
            max_num = max_num if max_num>= len(hash) else len(hash)
        return max_num

s = Solution()
print(s.lengthOfLongestSubstring('pwwkew'))
