cpp

``` cpp
#include <string>
#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        if(!s.size()){
            return 0;
        }
        else if(s.size() == 1){
            return 1;
        }

        vector<char> hash = {};
        int a = 0, b = 0;
        int max_num = 0;
        hash.push_back(s[0]);
        int nums = s.size();
        while(b < nums - 1){
            auto it = find(hash.begin(), hash.end(), s[b+1]);
            if(it != hash.end()){
                int tmp = it - hash.begin();
                hash.erase(hash.begin(), hash.begin() + tmp + 1);
                b++;
                hash.push_back(s[b]);
            }
            else{
                hash.push_back(s[b+1]);
                b++;
            }
            max_num = max_num>=hash.size() ? max_num : hash.size();
        }
        return max_num;
    }
};
