``` cpp
#include <opencv2/opencv.hpp>
#include <iostream>
#include <cstdio>

int main() {
    std::string input_url = 'rtmp://101.126.68.169/live/mac';
    std::string output_url = 'rtmp://101.126.68.169/live/processed';

    // æ‰“å¼€è¾“å…¥æµ
    cv::VideoCapture cap(input_url);
    if (!cap.isOpened()) {
        std::cerr << 'æ— æ³•æ‰“å¼€è¾“å…¥æµ: ' << input_url << std::endl;
        return -1;
    }

    int width  = static_cast<int>(cap.get(cv::CAP_PROP_FRAME_WIDTH));
    int height = static_cast<int>(cap.get(cv::CAP_PROP_FRAME_HEIGHT));
    int fps    = static_cast<int>(cap.get(cv::CAP_PROP_FPS));
    if (fps == 0) fps = 25; // æœ‰äº›æµæ— æ³•æ­£ç¡®è·å–fpsï¼Œç»™ä¸ªé»˜è®¤å€¼

    // å‡†å¤‡ ffmpeg å‘½ä»¤
    std::string ffmpeg_cmd =
        'ffmpeg -y -f rawvideo -pix_fmt bgr24 -s ' +
        std::to_string(width) + 'x' + std::to_string(height) +
        ' -r ' + std::to_string(fps) +
        ' -i - -c:v libx264 -pix_fmt yuv420p -preset ultrafast -f flv ' +
        output_url;

#ifdef _WIN32
    FILE* pipe = _popen(ffmpeg_cmd.c_str(), 'wb');
#else
    FILE* pipe = popen(ffmpeg_cmd.c_str(), 'w');
#endif

    if (!pipe) {
        std::cerr << 'æ— æ³•å¯åŠ¨ ffmpeg è¿›ç¨‹' << std::endl;
        return -1;
    }

    cv::Mat frame;
    while (true) {
        if (!cap.read(frame)) {
            std::cerr << 'è¯»å–å¸§å¤±è´¥ï¼Œç»“æŸ' << std::endl;
            break;
        }

        // å°†å¸§å†™å…¥ ffmpeg stdin
        fwrite(frame.data, 1, frame.total() * frame.elemSize(), pipe);
    }

#ifdef _WIN32
    _pclose(pipe);
#else
    pclose(pipe);
#endif

    cap.release();
    return 0;
}
